import pygame
import random
import sys
import time
import io
import base64

pygame.init()
pygame.font.init()
mainClock = pygame.time.Clock()
pygame.display.set_caption("Dice App")
screen = pygame.display.set_mode((800, 600))
font = pygame.font.SysFont("Sans", 30)
background_color = 'grey57'
#what we have here is just image strings in base64
img1 = ''
img1out = io.BytesIO(base64.b64decode(img1))
image1 = pygame.image.load(img1out)
img2 = ''
img2out = io.BytesIO(base64.b64decode(img2))
image2 = pygame.image.load(img2out)
img3 = ''
img3out = io.BytesIO(base64.b64decode(img3))
image3 = pygame.image.load(img3out)
img4 = ''
img4out = io.BytesIO(base64.b64decode(img4))
image4 = pygame.image.load(img4out)
img5 = ''
img5out = io.BytesIO(base64.b64decode(img5))
image5 = pygame.image.load(img5out)
img6 = ''
img6out = io.BytesIO(base64.b64decode(img6))
image6 = pygame.image.load(img6out)
img7 = ''
img7out = io.BytesIO(base64.b64decode(img7))
image7 = pygame.image.load(img7out)

dice_opts = [image6, image5, image4, image3, image2, image1]


def roll():
    dice = random.choice(dice_opts)
    return dice


def app_screen():
    screen.fill(background_color)
    screen.blit(image7, (610, 100))

    run = True
    while run:
        time_end = time.time() + 60 * 0.015
        for event in pygame.event.get():
            if event.type == pygame.QUIT:
                sys.exit()
            if event.type == pygame.KEYDOWN:
                if event.key == pygame.K_ESCAPE:
                    sys.exit()
                if event.key == pygame.K_SPACE:
                    while time.time() < time_end:
                        dice = roll()
                        screen.blit(dice, (50, 50))
                        pygame.display.update()

        pygame.display.update()
        mainClock.tick(60)


app_screen()
